import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { postApi, categoryApi, tagApi } from '../services/api';
import SearchBar from '../components/SearchBar';
import Pagination from '../components/Pagination';
import './PostList.css';

function PostList() {
  const [posts, setPosts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [tags, setTags] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const [pageSize] = useState(10);
  const [keyword, setKeyword] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedTag, setSelectedTag] = useState(null);

  // ÌÉÄÏù¥Ìïë Ïï†ÎãàÎ©îÏù¥ÏÖò
  const [displayTitle, setDisplayTitle] = useState('');
  const fullTitle = 'AI Dev Blog';

  useEffect(() => {
    let i = 0;
    const timer = setInterval(() => {
      if (i <= fullTitle.length) {
        setDisplayTitle(fullTitle.slice(0, i));
        i++;
      } else {
        clearInterval(timer);
      }
    }, 100);
    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    loadCategories();
    loadTags();
  }, []);

  useEffect(() => {
    loadPosts();
  }, [currentPage, keyword, selectedCategory, selectedTag]);

  const loadCategories = async () => {
    try {
      const response = await categoryApi.getAllCategories();
      setCategories(response.data);
    } catch (error) {
      console.error('Ïπ¥ÌÖåÍ≥†Î¶¨ Î°úÎî© Ïã§Ìå®:', error);
    }
  };

  const loadTags = async () => {
    try {
      const response = await tagApi.getAllTags();
      setTags(response.data);
    } catch (error) {
      console.error('ÌÉúÍ∑∏ Î°úÎî© Ïã§Ìå®:', error);
    }
  };

  const loadPosts = async () => {
    try {
      setLoading(true);
      const response = await postApi.getAllPosts(
        currentPage,
        pageSize,
        keyword,
        selectedCategory,
        selectedTag
      );
      setPosts(response.data.content);
      setTotalPages(response.data.totalPages);
      setLoading(false);
    } catch (error) {
      console.error('Í≤åÏãúÍ∏Ä Î°úÎî© Ïã§Ìå®:', error);
      setLoading(false);
    }
  };

  const handleSearch = (searchKeyword) => {
    setKeyword(searchKeyword);
    setSelectedCategory(null);
    setSelectedTag(null);
    setCurrentPage(0);
  };

  const handleCategoryFilter = (categoryId) => {
    setSelectedCategory(categoryId === selectedCategory ? null : categoryId);
    setKeyword('');
    setSelectedTag(null);
    setCurrentPage(0);
  };

  const handleTagFilter = (tagName) => {
    setSelectedTag(tagName === selectedTag ? null : tagName);
    setKeyword('');
    setSelectedCategory(null);
    setCurrentPage(0);
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('ko-KR', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
    }).replace(/\. /g, '.').replace('.', '');
  };

  const getCategoryColor = (categoryName) => {
    const colors = {
      'AI': '#a855f7',
      'Backend': '#00d9ff',
      'Frontend': '#00ff88',
    };
    return colors[categoryName] || '#f97316';
  };

  const getFilterTitle = () => {
    if (keyword) return `Í≤ÄÏÉâ: "${keyword}"`;
    if (selectedCategory) {
      const category = categories.find(c => c.id === selectedCategory);
      return `${category?.name}`;
    }
    if (selectedTag) return `#${selectedTag}`;
    return 'All Posts';
  };

