import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { postApi, categoryApi } from '../services/api';
import MarkdownViewer from '../components/MarkdownViewer';
import './PostForm.css';

function PostForm() {
  const { id } = useParams();
  const navigate = useNavigate();
  const isEditMode = Boolean(id);

  const [formData, setFormData] = useState({
    title: '',
    content: '',
    author: '',
    categoryId: '',
    tags: [],
  });

  const [categories, setCategories] = useState([]);
  const [tagInput, setTagInput] = useState('');
  const [errors, setErrors] = useState({});
  const [showPreview, setShowPreview] = useState(false);

  useEffect(() => {
    loadCategories();
    if (isEditMode) {
      loadPost();
    }
  }, [id]);

  const loadCategories = async () => {
    try {
      const response = await categoryApi.getAllCategories();
      setCategories(response.data);
    } catch (error) {
      console.error('Ïπ¥ÌÖåÍ≥†Î¶¨ Î°úÎî© Ïã§Ìå®:', error);
    }
  };

  const loadPost = async () => {
    try {
      const response = await postApi.getPost(id);
      setFormData({
        title: response.data.title,
        content: response.data.content,
        author: response.data.author,
        categoryId: response.data.category?.id || '',
        tags: response.data.tags ? response.data.tags.map(t => t.name) : [],
      });
    } catch (error) {
      console.error('Í≤åÏãúÍ∏Ä Î°úÎî© Ïã§Ìå®:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: '',
      });
    }
  };

  const handleAddTag = (e) => {
    if (e.key === 'Enter' && tagInput.trim()) {
      e.preventDefault();
      if (!formData.tags.includes(tagInput.trim())) {
        setFormData({
          ...formData,
          tags: [...formData.tags, tagInput.trim()],
        });
      }
      setTagInput('');
    }
  };

  const handleRemoveTag = (tagToRemove) => {
    setFormData({
      ...formData,
      tags: formData.tags.filter(tag => tag !== tagToRemove),
    });
  };

  const validate = () => {
    const newErrors = {};
    if (!formData.title.trim()) {
      newErrors.title = 'Ï†úÎ™©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî';
    }
    if (!formData.content.trim()) {
      newErrors.content = 'ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî';
    }
    if (!formData.author.trim()) {
      newErrors.author = 'ÏûëÏÑ±ÏûêÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî';
    }
    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newErrors = validate();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    try {
      const submitData = {
        ...formData,
        categoryId: formData.categoryId || null,
        tags: formData.tags.length > 0 ? formData.tags : null,
      };

      if (isEditMode) {
        await postApi.updatePost(id, submitData);
      } else {
        await postApi.createPost(submitData);
      }
      navigate('/');
    } catch (error) {
      console.error('Ï†ÄÏû• Ïã§Ìå®:', error);
      alert('Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  const insertMarkdown = (syntax) => {
    const textarea = document.getElementById('content');
    const start = textarea.selectionStart;
    const end = textarea.selectionEnd;
    const selectedText = formData.content.substring(start, end);

    let newText = formData.content;
    let cursorPos = start;

    switch(syntax) {
      case 'bold':
        newText = formData.content.substring(0, start) + `**${selectedText || 'ÍµµÏùÄ ÌÖçÏä§Ìä∏'}**` + formData.content.substring(end);
